<script>
import { mapGetters, mapActions, mapState } from 'vuex'
import avatar from './mobileElements/avatar.vue'
import dateTime from './mobileElements/dateTime.vue'
import onlineCounter from './mobileElements/onlineCounter.vue'

export default {
  components: {
    avatar,
    dateTime,
    onlineCounter,
  },
  data() {
    return {
      iconCodeSelection: 'us',
      onlineOrdersCount: 0,
      dm: this.baseurl('delivery') + '/delivery_home/new',
      dashboard: this.baseurl('dashboard'),
      crm: this.baseurl('crm') + '/brand_customers',
      menu: this.baseurl('menu'),
      brand: this.baseurl('brands'),
    }
  },
  computed: {
    ...mapGetters(['profileHendler']),
    ...mapActions('auth', ['logout']),
    ...mapGetters('context', ['store', 'transactions']),
    ...mapGetters('location', ['_t']),
    ...mapGetters('auth', ['waiter', 'carhop', 'allowed']),
    ...mapState('location', ['availableLanguages', 'language']),
    ...mapState('dinein', ['dineInTabType', 'activeArea']),
    ...mapState('sync', ['online']),
    ...mapGetters({
      brandContext: ['context/brand'],
    }),
    ...mapState({
      latestOnlineOrders: state =>
        state.order.onlineOrders ? state.order.onlineOrders.length : 0,
      username: state =>
        state.auth && state.auth.userDetails ? state.auth.userDetails.name : '',
    }),
    ...mapGetters('location', ['_t', 'permitted']),
    vlocale: {
      get() {
        return this.$store.state.location.locale
      },
      set(val) {
        return this.$store.commit('location/SET_LOCALE', val)
      },
    },
  },
  methods: {
    profileHendlerChange() {
      this.$store.dispatch('profileHendlerChange')
    },
    mlogout() {
      this.$store.dispatch('mobileLogout')
      this.$store.dispatch('userCalcHendlerChange')
      this.$store.dispatch('openUserHendlerChange')
      this.$store.dispatch('profileHendlerChange')
      // this.logout()
      this.$store.dispatch('auth/logout')
    },
    baseurl(link) {
      /*DeliveryManager, Dinein*/
      let replaceURI = ''
      if (this.$router.currentRoute.name === 'DeliveryManager') {
        replaceURI = '/delivery-manager/'
      } else if (this.$router.currentRoute.name === 'Dinein') {
        replaceURI = '/dine-in/'
      }
      return (
        window.location.href.replace(
          new RegExp('(/pos)?' + replaceURI + '.*'),
          '/' + link
        ) + this.$store.getters['context/brand']
      )
    },
    enabledModule(option) {
      switch (option) {
        case 'switchCashier':
          return true
      }
    },
    walkOrder() {
      this.$router.push(this.store)
      this.$store.commit('order/ORDER_TYPE', {
        OTview: 'Walk In',
        OTApi: 'walk_in',
      })
    },
    moveTransactionSection() {
      this.$router.push(this.store + '/transactions')
    },
  },
}
</script>
